package com.jerome.rtp.protocol.utils;import java.nio.charset.Charset;import org.glassfish.grizzly.Buffer;/** * 处理 buffer 工具类. *   * @author Will.jingmiao * */public class BufferUtils {	public static final Charset UTF_8 = Charset.forName("UTF-8");		public static int readableBytes(Buffer buffer){		return buffer.limit()-buffer.position();	}	public static void skipBytes(Buffer buffer,int l){		buffer.get(new byte[l]);	}		public static long getOneUnsignedInt(Buffer buffer){		return ((long)buffer.getInt())&0xFFFFFFFFL;	}	public static short getOneUnsignedByte(Buffer buffer){		return (short)(buffer.get()&0x0FF);	}		public static int getOneUnsignedShort(Buffer buffer){		return ((int)buffer.getShort())&0xFFFF;	}	/**	 * 组团处理 三个字节的 medium类型.	 * 	 * @param buffer	 * @return	 */	public static int getMedium(Buffer buffer){		byte b = buffer.get();		short st = buffer.getShort();		int i=b<<8|st;		return i;	}	public static Object[] toMedium(int i){		Object[] obj = new Object[2];				obj[0]=(byte)(i>>8);		obj[1]=(short)i;		return obj;	}}