package com.jerome.rtp.protocol.utils;import java.net.InetAddress;import java.net.UnknownHostException;public class RtpNetUtil {	public static String getGenerateCNAME() throws UnknownHostException {		String hostname = null;		String cname = "";		InetAddress host = InetAddress.getLocalHost();		hostname = host.getHostName();		cname = System.getProperty("user.name");		if (cname == null) {			return hostname;		} else {			return cname + "@" + hostname;		}	}		public static long getGenerateSsrc() {		long rand = TrueRandom.rand();		return rand;		}}