package com.jerome.rtp.relay;public class RtpConfigure {	private String Localhost;	private int portBegin;	private int portEnd;	public static RtpConfigure newInstance(String localhost, int portBegin, int portEnd) {		RtpConfigure config = new RtpConfigure();		config.Localhost = localhost;		config.portBegin = portBegin;		config.portEnd = portEnd;		return config;	}	public String getLocalhost() {		return Localhost;	}	public int getPortBegin() {		return portBegin;	}	public int getPortEnd() {		return portEnd;	}}