package com.jerome.rtp.relay.session;import org.glassfish.grizzly.nio.transport.UDPNIOServerConnection;import com.jerome.rtp.protocol.ControlPacketHandler;import com.jerome.rtp.protocol.DataPacketHandler;/** * rtp session 接口. *  * 获取session rtp rtcp远端地址连接.<br> * 获取rtp本地端口.rtcp=rtp+1; *  * @author Will.jingmiao * @version 创建时间：2014-5-29 下午5:05:09 类说明 */public interface RtpSession extends DataPacketHandler, ControlPacketHandler {	UDPNIOServerConnection getDataConnect();	UDPNIOServerConnection getControlConnect();	int getRtpPort();		long getSessionId();		long getLastSessionTime();		boolean isStart();		void stop();			void setSessionListener(SessionListener listener);	}