package com.jerome.rtp.relay.transponder;import java.net.URI;/** *  * Rtp/Rtcp中继转发器. *  * @author Will.jingmiao *  */public interface RtpTransponder {		void start(final String localhost,final int portBegin,final int portEnd);	long createRtpSession();		URI bind(final String key, long sessionId);	boolean unbind(final String key, long sessionId);		boolean closeSession(long sessionId);}